import { Component } from '@angular/core';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {

}
<p>cart works!</p>
<!DOCTYPE html>
<html>
<head>
    <title>Shopping Cart Example</title>
</head>
<body>
    <!-- Product information -->
    <div class="product">
        <h2>Product Name: Example Product</h2>
        <p>Price: $19.99</p>
        <button class="add-to-cart-button" onclick="addToCart('Example Product', 19.99)">Add to Cart</button>
    </div>

    <!-- Cart -->
    <h2>Shopping Cart</h2>
    <ul id="cart"></ul>

    <script>
        // Cart items will be stored in an array
        const cartItems = [];

        // Function to add a product to the cart
        function addToCart(productName, price) {
            const item = {
                name: productName,
                price: price
            };

            cartItems.push(item);

            // Display the updated cart
            displayCart();
        }

        // Function to display the cart
        function displayCart() {
            const cartList = document.getElementById("cart");

            // Clear the current cart display
            cartList.innerHTML = "";

            // Iterate through the cart items and display them
            cartItems.forEach(item => {
                const listItem = document.createElement("li");
                listItem.textContent = `${item.name} - $${item.price}`;
                cartList.appendChild(listItem);
            });
        }
    </script>
</body>
</html>
