import { Component } from '@angular/core';
import { Product } from '../product/product';
import { Router } from '@angular/router';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  products:Product[]=[];


  constructor(private productService:ProductService ,private router:Router){
    this.productService.getProducts().subscribe(data=>{
      console.log(data)
      this.products = data as Product[];      })
  }
}
<div *ngIf="products.length > 0 else nohotel"  >
    <p style="margin-left: 10%; padding-left: 5px;" >
        <marquee><i>All Hotels Are here:</i></marquee></p>
    <div class="alert alert-dark"  *ngFor="let hotel of hotels">
      <!-- <img class="card-img-top" src="{{hotel.pic}}" alt="Card image cap"> -->
      <!-- <img class="card-img-top" class="card" style="width: 18rem;" [src]="hotel.pic" alt="Card image cap"> -->
      <!-- <img class="card-img-top" class="card" style="width: 18rem;" [src]="hotel.pic" alt="Card image cap"> -->
       <br>
       Hotel Id: {{hotel.id}}
        <br/>
        Hotel Name : {{hotel.name}}
        <br/>
        Hotel location : {{hotel.location}}
        <br/>
        <!-- <img class="card-img-top" class="card" style="width: 18rem;" [src]="hotel.pic" alt="Card image cap"> -->

    </div>
</div>
    <!-- for else part we have to use ng-tempalte -->
<ng-template #nohotel >
    <div class="alert alert-warning">
        No Hotels yet
    </div>
</ng-template>
