import { Component } from '@angular/core';
import { Product } from '../product/product';
import { Router } from '@angular/router';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  products:Product[]=[];


  constructor(private productService:ProductService ,private router:Router){
    this.productService.getProducts().subscribe(data=>{
      console.log(data)
      this.products = data as Product[];      })
  }
}



<!DOCTYPE html>

<body class="pageStyle">
<div *ngIf="products.length > 0 else noproduct"  class="card" >
    <p style="margin-left: 10%; padding-left: 5px;" >
        <marquee><i>All Products Are here:</i></marquee></p>
    <div class="alert alert-dark"  *ngFor="let product of products">

         <img class="card-img-top"  src={{product.pic}}  alt="Card image cap">


        <br>
       Product Id: {{product.id}}
        <br/>
        Product Name: {{product.name}}
        <br/>
        Product Price: {{product.price}}
        <br/>
        Product Quantity: {{product.quantity}}
        <br/>





    </div>
</div>
    <!-- for else part we have to use ng-tempalte -->
<ng-template #noproduct >
    <div class="alert alert-warning">
        No Products yet
    </div>
</ng-template>
