import { Cart } from './cart';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  cart: any[] = [];
constructor(){

}

// Cart items will be stored in an array

addToCart(product: any) {
  this.cart.push(product);
}


}
