import { Component } from '@angular/core';
import { Cart } from './cart';
import { Product } from '../product/product';
import { ProductService } from '../services/product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {

  product:Product= new Product();
  products:Product[]=[];
  constructor(private productService:ProductService,private router:Router){
    this.productService.getProducts().subscribe(htl=>{
      this.rooms = htl as Room[];
    })
  }
  selectChange(hid:any){
    for (let index = 0; index < this.rooms .length; index++) {
      if(this.rooms [index].roomNo==hid)
      {
        this.room= this.rooms[index];
        break;
      }

    }
  }



  deleteRoom(){
    this.roomService.deleteRoom(this.room.roomNo).subscribe(htl=>{
      if(htl){
        alert(" hotel deleted successfully")
        this.router.navigateByUrl("rooms");
      }
    })
    this.room = new Room();
  }


}
